function TxtProBlueMetal2(text) {
	return new Promise((resolve, reject) => {
	  zrapi 
    .textpro(type.blue_metal2, [ text ])
    .then((url) => {
      const result = {
        status_code: 200,
        coder: '@ariasu.xyz',
        date: Date(),
        result: {
          text: text,
          scraping: type.blue_metal2,
          output: url
        }
      }
      resolve(result)
    })
    .catch(reject)
	})
}
